import "./login.css"
import { useRef } from "react";

export default function Login() {
    const email = useRef();
    const password = useRef();

    const handleClick = (e) => {
        e.preventDefault();
        console.log(email.current.value);
    }


    return (
        <div className="login">
            <div className="loginWrapper">
                <div className="loginLeft">
                    <h3 className="loginLogo">Social</h3>
                    <span className="loginDesc">Connect with friends and talk about your book. </span>

                </div>
                <div className="loginRight">
                    <form className="loginBox" onSubmit={handleClick}>
                        <input type="email" className="loginInput" placeholder="Email" ref={email} required />
                        <input type="password" className="loginInput" placeholder="Password" ref={password} required minLength="6" />
                        <button className="loginButton">Login</button>
                        <span className="loginForgot">Forgot Password?</span>
                        <button className="loginRegisterButton">Create a New Account</button>
                    </form>
                </div>

            </div>

        </div>
    )
}
